import "./app.css";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import { Container } from "react-bootstrap";
import { useState } from "react";
import Accord from "./Accord";

function App() {
  const [addButton, setaddButton] = useState("Add");
  const [title, settitle] = useState("");
  const [description, setDescription] = useState("");

  const [addItem, setaddItem] = useState([]);
  const handleAdd = () => {
    console.log(addItem);
    if (addButton === "Add") {
      setaddButton("Cancel");
    } else {
      setaddButton("Add");
    }
  };

  const handleSubmit = (e) => {
    const newStruct = { title: title, description: description };
    console.log(newStruct);
    setaddItem([...addItem, newStruct]);
    console.log(addItem);
    handleAdd();
  };

  const validateForm = () => {
    return title.length > 0 && description.length > 0;
  };

  return (
    <>
      <Container>
        <div style={{ textAlign: "center" }}>
          <h1>Todo List</h1>
        </div>

        <Button variant="outline-dark" onClick={handleAdd}>
          {addButton}
        </Button>

        {addButton === "Cancel" && (
          <Form style={{ marginTop: "20px" }} onSubmit={handleSubmit}>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label>Title</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter title"
                value={title}
                onChange={(e) => {
                  settitle(e.target.value);
                }}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="formBasicPassword">
              <Form.Label>Description</Form.Label>
              <Form.Control
                type="textarea"
                placeholder="Enter Description"
                value={description}
                onChange={(e) => {
                  setDescription(e.target.value);
                }}
              />
            </Form.Group>

            <Button variant="dark" type="submit" disabled={!validateForm}>
              Add in List
            </Button>
          </Form>
        )}

        {addItem.length > 0 && addItem.map((item) => <Accord kprops={item} />)}
      </Container>
    </>
  );
}

export default App;
