import { Button, Col, Row } from "react-bootstrap";
import Accordion from "react-bootstrap/Accordion";
import "./accord.css";
import { useState } from "react";
import { Form } from "react-bootstrap";
export default function Accord({ props }) {
  const [updateButton, setUpdateButton] = useState("Update");
  
  const [title, setTitle] = useState(props.body.title);
  const [description, setDescription] = useState(props.body.description);

  const handleUpdate = () =>{
    if (updateButton === "Update")
    {
      setUpdateButton("Cancel")
    }

  }
  return (
    <>
      <Row className="new_row">
        <Col lg={8}>
          <Accordion className="accord">
            <Accordion.Item eventKey="0">
              <Accordion.Header>{props.body.title}</Accordion.Header>
              <Accordion.Body>{props.body.description}</Accordion.Body>
            </Accordion.Item>
          </Accordion>
        </Col>
        <Col lg={1}>
          <Button
            variant="outline-danger"
            className="delButton"
            onClick={() => props.handler(props.key)}
          >
            Delete
          </Button>
        </Col>
        <Col lg={3}>
          <Button variant="outline-primary" className="updateButton" onClick={handleUpdate}>
            {updateButton}
          </Button>
        </Col>
      </Row>
      {updateButton === "Cancel" &&
      <>
      <Form className="form">
              <Form.Group className="mb-3 " controlId="formBasicEmail">
                <Form.Label>Title</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter title"
                  value={title}
                  onChange={(e) => {
                    setTitle(e.target.value);
                  }}
                />
              </Form.Group>

              <Form.Group className="mb-3" controlId="formBasicPassword">
                <Form.Label>Description</Form.Label>
                <Form.Control
                  type="textarea"
                  placeholder="Enter Description"
                  value={description}
                  onChange={(e) => {
                    setDescription(e.target.value);
                  }}
                />
              </Form.Group>

              <Button variant="dark" type="submit">
                Update
              </Button>
            </Form>

      </>}
    </>
  );
}
